    def find(self, full_org, director_name, adress, inn, kpp, short_org):
        # finding the current item index  in combo box
        index = self.comboBox.currentIndex()

        # showing content on the screen though label
        print("Индекс: " + str(index))
        self.put_text(full_org, director_name, adress, inn, kpp, short_org, index)

    def put_text(self, full_org,director_name, adress, inn, kpp, short_org, index):
        self.lineEdit_fullorg.setText(full_org[index])
        self.lineEdit_director_name.setText(director_name[index])
        self.lineEdit_adress.setText(adress[index])
        self.lineEdit_inn.setText(inn[index])
        self.lineEdit_kpp.setText(kpp[index])
        self.lineEdit_short_org.setText(short_org[index])

    def combox(self, name, full_org, director_name, adress, inn, kpp, short_org, index):
        for i in range(len(name)):
            self.comboBox.addItem(name[i])
        self.pushButton_orgs.pressed.connect(lambda: self.find(full_org, director_name, adress,  inn, kpp, short_org))



